sys_ui_action.script:
function x_adosy_as_openStamp() {

	g_form.addInfoMessage('x_adosy_as_openStamp');

	var table = g_form.getTableName(); 
	var sys_id = g_form.getUniqueValue();
	var modal = new GlideModal('x_adosy_as_stamp'); // service portal page. Widget is Adobe Acrobat Sign Send for Signature
	modal.setTitle('Invoice Stamp with Adobe Acrobat Sign');
	modal.setSize('lg');
	modal.setBackdropStatic(true);
	modal.setAutoFullHeight(true);

	var agreement_name = "BIT Procurement Approval Stamp: " + g_form.getValue('short_description') + " (" + g_form.getValue('number') + ")"; // set subject

	var templates = [];

	templates.push('375147791baab150baac766e034bcbeb'); // adobe document template (x_adosy_as_document_template) sysid 

	var post_submit_link = 'https://www.adobe.com/';

	g_form.addInfoMessage('modal.renderIframe');

	//modal.renderIframe('/$sp.do?id=x_adosy_as_send&subject_table=' + table + '&subject_id=' + sys_id);
	modal.renderIframe('/$sp.do?id=x_adosy_as_stamp&subject_table=' + table + '&subject_id=' + sys_id + '&agreement_name=' + agreement_name + '&templates=' + templates + '&post_submit_link' + post_submit_link);

	/*
    // Replace 'your_record_producer_sys_id' with the actual sys_id of your record producer
    var recordProducerSysId = 'f3a65fb11b2eb150baac766e034bcb26'; // 'your_record_producer_sys_id';

    // Generate the URL for the record producer
    var recordProducerUrl = '/com.glideapp.servicecatalog_cat_item_view.do?sysparm_id=' + recordProducerSysId;

    // Redirect to the record producer
    window.location.href = recordProducerUrl;	
	*/
}
